import { Component, Input } from "@angular/core";
import { Task } from "src/app/Task";

@Component({
  selector: "app-task-item",
  templateUrl: "./task-item.component.html",
  styleUrls: ["./task-item.component.css"],
})
export class TaskItemComponent {
  @Input() task!: Task;
}
<div class="task">
    <h3>{{ task.text }}</h3>
</div>
